import React, { useState } from "react";

export default function Person2() {
  let [name, setName] = useState("Hyder");

  const clickHandler = () => {
    setName("Someone");
  };

  return (
    <div>
      <p>{name}</p>
      <button onClick={clickHandler}>Change</button>
    </div>
  );
}
