import { render } from "@testing-library/react";
import React, { useState } from "react";

import Display from "./components/css_module_counter/Display";

import ErrorBoundary from "./ErrorBoundary/ErrorBoundary";

export default function App() {
  let [count, setCount] = useState(0);

  setTimeout(() => {
    setCount(count + 1);
  }, 1000);

  function renderDisplay() {
    if (count > 10) {
      throw new Error("Count Greater than 40");
    } else return <Display count={count} />;
  }

  return <ErrorBoundary>{renderDisplay()}</ErrorBoundary>;
}
