import React, { useEffect, useState } from "react";

import styles from "./Display.module.css";

export default function Display(props) {
  let [inner, setInner] = useState(0);
  useEffect(() => {
    console.log("[Display] (useEffect)", "count:", props.count);
  }, []);
  return (
    <div>
      <div className={styles.display}> {props.count} </div>
      <button
        onClick={() => {
          setInner(inner + 1);
        }}>
        Update Inner
      </button>
    </div>
  );
}
