import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);

    console.log("[App.js] Constructor Running!");

    this.state = {
      count: 0,
    };
  }

  static getDerivedStateFromProps(props, state) {
    console.log(props, state);
    console.log("[App.js] getDerievedStateFromProps Running!");
  }

  componentDidMount() {
    console.log("[App.js] componentDidMount");

    setTimeout(() => {
      this.setState({ count: this.state.count + 1 });
    }, 3000);
  }

  componentDidUpdate() {
    console.log("[App.js] ComponentDidUpdate");
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log("[App.js] shouldComponentUpdate", nextState);
    return true;
  }

  render() {
    console.log("[App.js] Render Method Running!");

    return <div>Hello from App.JS - Count {this.state.count} </div>;
  }
}

export default App;
