import React, { useState } from "react";

import Counter from "../components/Counter";

export default function App() {
  let [count1, setCount1] = useState(0);
  let [count2, setCount2] = useState(0);

  let incrementHandler = (number) => {
    switch (number) {
      case 1:
        setCount1(count1 + 1);
        break;

      case 2:
        setCount2(count2 + 1);
        break;

      default:
        return;
    }
  };

  return (
    <div className='main_wrapper'>
      <Counter
        count={count1}
        incHandler={() => {
          incrementHandler(1);
        }}
        name={1}
      />

      <Counter
        count={count2}
        incHandler={() => {
          incrementHandler(1);
        }}
        name={2}
      />
    </div>
  );
}
