import React, { useState } from "react";

export default function Display(props) {
  let [show, setShow] = useState(true);
  let [people, setPeople] = useState([
    {
      name: "Hyder",
    },
    {
      name: "Jan",
    },
    { name: "Manan" },
  ]);

  let renderList = () => {
    if (show) {
      return people.map((person) => {
        return (
          <div>
            <p> {person.name} </p>
          </div>
        );
      });
    } else return null;
  };

  return (
    <div>
      {renderList()}
      <input class='display' disabled='true' value={props.count} />
    </div>
  );
}
