import React, { useState } from "react";

import Display from "../components/Display";

export default function App() {
  let [count, setCount] = useState(0);

  let incrementHandler = () => {
    setCount(count + 1);
  };
  return (
    <div className='App'>
      <Display count={count} />
      <button
        onClick={() => {
          incrementHandler();
        }}>
        {" "}
        Increment{" "}
      </button>
    </div>
  );
}
