import React, { useEffect, useState } from "react";

import styles from "./Display.module.css";

export default function Display(props) {
  let [inner, setInner] = useState(0);

  /* ComponentDidMount */

  useEffect(() => {
    console.log("[Display] (useEffect) ~ componentDidMount");
    return () => {
      console.log("[Display] (useEffect) ~ componentWillUnmount , Cleaning Up");
    };
  }, []);

  useEffect(() => {
    console.log("[Display] (useEffect) ~ componentDidUpdate", "inner :", inner);
    return () => {
      console.log(" Returning Function ");
    };
  }, [inner]);

  return (
    <div>
      <div className={styles.display}> {props.count} </div>
      <button
        onClick={() => {
          setInner(inner + 1);
        }}>
        Update Inner
      </button>
    </div>
  );
}
