import React from "react";

function controls(props) {
  const styles = {
    counterUp: {
      backgroundColor: "green",
      color: "white",
      tranistion: "transform all .2s",
      ":hover": {
        transform: "scale(0.8)",
      },
    },

    counterDown: {
      backgroundColor: "red",
      color: "white",
    },
  };

  return (
    <div className='controls'>
      <button
        style={styles.counterUp}
        onClick={props.up}
        className='counter_up'>
        Counter + 1
      </button>
      <button
        style={styles.counterDown}
        onClick={props.down}
        className='counter_down'>
        Counter - 1
      </button>

      <input
        onKeyPress={(e) => {
          if (e.key === "Enter") {
            let value = parseInt(e.target.value);
            props.textInc(value);
          }
        }}
      />
    </div>
  );
}

export default controls;
