import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);

    console.log("[App.js] Constructor Running!");

    this.state = {
      count: 0,
    };
  }

  static getDerievedStateFromProps(props, state) {
    console.log(props, state);
    console.log("[App.js] getDerievedStateFromProps Running!");
  }

  componentDidMount() {
    console.log("[App.js] componentDidMount");
    setTimeout(() => {
      this.setState({ count: 1 }, 2000);
    });
  }

  render() {
    console.log("[App.js] Render Method Running!");

    return <div>Hello from App.JS</div>;
  }
}

export default App;
