import React from "react";

class Person extends React.Component {
  state = {
    name: "Hyder",
  };

  clickHandler = () => {
    this.setState({ name: "Me" });
  };

  render() {
    return (
      <div>
        <p> My name is {this.state.name}</p>
      </div>
    );
  }
}

export default Person;
