import React, { Component } from "react";

import styles from "./Counter.module.css";

export default class Counter extends Component {
  constructor(props) {
    super(props);

    this.state = {
      color: "",
    };

    switch (this.props.name) {
      case 1:
        this.setState({ color: "red" });
        break;

      case 2:
        this.setState({ color: "green" });
        break;

      default:
        return;
    }
  }

  componentDidMount() {
    console.log(
      `[Couner ${this.props.name}] componentDidMount`,
      this.state.color
    );
  }

  componentDidUpdate() {
    console.log(`[Couner ${this.props.name}] componentDidUpdate`);
  }

  render() {
    console.log(`[Couner ${this.props.name}] rendering`);
    return (
      <div className={styles.wrapper}>
        <div className={styles.name}>Counter {this.props.name}</div>
        <div className={styles.display}>{this.props.count}</div>
        <div className={styles.controls}>
          <button className={styles.green} onClick={this.props.incHandler}>
            Increment
          </button>
          <button className={styles.red}>Decrement</button>
        </div>
      </div>
    );
  }
}
