import React, { useState } from "react";

import styled from "styled-components";

function Counter_Styled() {
  let [count, setCount] = useState(0);

  let Div = styled.div`
    margin: auto auto;
    margin-top: 60px;
  `;

  let Counter_Display = styled.div`
    font-size: 20px;
    color: gray;
  `;

  let IncreaseBtn = styled.button`
    background-color: green;
    padding: 20px;
    color: white;

    hover: {
      transform: scale(0.8);
    }
  `;
  return (
    <Div>
      <Counter_Display>{count}</Counter_Display>

      <div>
        <IncreaseBtn
          onClick={(e) => {
            setCount(count + 1);
          }}>
          Increase Counter
        </IncreaseBtn>
      </div>
    </Div>
  );
}

export default Counter_Styled;
