import React, { useState } from "react";

import Counter from "../components/Counter";

export default class App extends React.Component {
  state = {
    count1: 0,
    count2: 0,
  };

  incrementHandler = (number) => {
    switch (number) {
      case 1:
        setCount1(count1 + 1);
        break;

      case 2:
        setCount2(count2 + 1);
        break;

      default:
        return;
    }
  };

  render() {
    return (
      <div className='main_wrapper'>
        <Counter
          count={count1}
          incHandler={() => {
            incrementHandler(1);
          }}
          name={1}
        />

        <Counter
          count={count2}
          incHandler={() => {
            incrementHandler(2);
          }}
          name={2}
        />
      </div>
    );
  }
}
