import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";

import Display from "./components/display";
import Controls from "./components/controls";

function App() {
  let [count, setCount] = useState(0);

  let countUpHandler = () => {
    setCount(count + 1);
  };

  let countDownHandler = () => {
    setCount(count - 1);
  };
  return (
    <div className='App'>
      <Display count={count} />
      <Controls
        up={() => {
          countUpHandler();
        }}
        down={() => {
          countDownHandler();
        }}
      />
    </div>
  );
}

export default App;
